Date.prototype.Format = function (fmt) {
    var o = {
        "M+": this.getMonth() + 1,//月份
        "d+": this.getDate(),//天
        "h+": this.getHours(),//小时
        "m+": this.getMinutes(),//分钟
        "s+": this.getSeconds(),//秒
        "S": this.getMilliseconds()//毫秒
    };
    if (/(y+)/.test(fmt)) fmt = fmt.replace(RegExp.$1, (this.getFullYear() + "").substr(4 - RegExp.$1.length));
    for (var k in o) {
        if (new RegExp("(" + k + ")").test(fmt))
            fmt = fmt.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));
    }
    return fmt;

};

var util = util || {};
util.format = {
    ToInt: function (value) {
        try
        {
            if (value == undefined || value == '')
                return value;
            return parseInt(value);
        }
        catch(e){
            return value;
        }

    },
    ToDouble: function (value, prec) {
        try{
            if (value == undefined || value == '')
                return value;
            var fixnum = parseFloat(value).toFixed(prec);
            return fixnum;
        }
        catch (e) {
            return value;
        }
    },
    ToPercent: function (value, perc) {
        try{
            if (value == undefined || value == '')
                return value;
            var fixnum = (parseFloat(value) * 100).toFixed(perc);
            return fixnum;
        }
        catch (e) {
            return value;
        }
    },
    ToDateStr:function(value,fmt){
        var fmtDate = value;
        try{
            fmtDate = new Date(value).Format(fmt);
        }catch(ex){
            fmtDate = value;
        }
        return fmtDate;
    }
}

module.exports = util;
